import React, { useState, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { X, ChevronDown, MapPin, Globe, ArrowRight, Star, CheckCircle, Hammer, Zap, Settings, HardHat, Pill, Wine, Shield, ArrowLeft } from 'lucide-react'; // Importa Digging
import articulacionesData from '../data/articulaciones.json';

const ConsultaModal = ({ isOpen, onClose }) => {
  const [step, setStep] = useState(0); // Inicia en el paso 0
  const [selectedRegion, setSelectedRegion] = useState('');
  const [selectedDepartamento, setSelectedDepartamento] = useState('');
  const [selectedEscuela, setSelectedEscuela] = useState('');
  const [selectedOrientacion, setSelectedOrientacion] = useState('');
  const [resultado, setResultado] = useState(null);
  const [isDark, setIsDark] = useState(false);

  useEffect(() => {
    const darkMode = window.matchMedia('(prefers-color-scheme: dark)').matches;
    setIsDark(darkMode);
  }, []);

  const regiones = [...new Set(articulacionesData.map(item => item.region))];

  const departamentosFiltrados = articulacionesData
    .filter(item => item.region === selectedRegion)
    .map(item => item.departamento)
    .filter((depto, index, arr) => arr.indexOf(depto) === index);

  const escuelasFiltradas = articulacionesData
    .filter(item => item.departamento === selectedDepartamento)
    .map(item => item.escuela)
    .filter((escuela, index, arr) => arr.indexOf(escuela) === index);

  const orientacionesFiltradas = articulacionesData
    .filter(item => item.departamento === selectedDepartamento && item.escuela === selectedEscuela)
    .map(item => item.orientacion);

  const handleRegionSelect = (region) => {
    setSelectedRegion(region);
    setSelectedDepartamento('');
    setSelectedEscuela('');
    setSelectedOrientacion('');
    setStep(1);
  };

  const handleDepartamentoSelect = (depto) => {
    setSelectedDepartamento(depto);
    setSelectedEscuela('');
    setSelectedOrientacion('');
    setStep(2);
  };

  const handleEscuelaSelect = (escuela) => {
    setSelectedEscuela(escuela);
    setSelectedOrientacion('');
    setStep(3);
  };

  const handleOrientacionSelect = (orientacion) => {
    setSelectedOrientacion(orientacion);
    const result = articulacionesData.find(
      item => item.departamento === selectedDepartamento && 
               item.escuela === selectedEscuela && 
               item.orientacion === orientacion
    );
    setResultado(result);
    setStep(4);
  };

  const resetModal = () => {
    setStep(0); // Reinicia al paso 0
    setSelectedRegion('');
    setSelectedDepartamento('');
    setSelectedEscuela('');
    setSelectedOrientacion('');
    setResultado(null);
  };

  const handleClose = () => {
    resetModal();
    onClose();
  };

  const handleBack = () => {
    if (step > 0) {
      setStep(step - 1);
      if (step === 1) setSelectedRegion('');
      if (step === 2) setSelectedDepartamento('');
      if (step === 3) setSelectedEscuela('');
      if (step === 4) setSelectedOrientacion('');
      setResultado(null);
    }
  };

  if (!isOpen) return null;

  const getIconComponent = (iconName) => {
    switch (iconName) {
      case 'Zap': return Zap;
      case 'Hammer': return Hammer;
      case 'Settings': return Settings;
      case 'HardHat': return HardHat;
      case 'Pill': return Pill;
      case 'Wine': return Wine;
      case 'Shield': return Shield;

